CREATE TABLE TBL_PM_INDUSTRY_TYPE (CODE VARCHAR(30) NOT NULL ,NAME VARCHAR(255) NOT NULL ,CONSTRAINT TBL_PM_INDUSTRY_TYPE_PK PRIMARY KEY (CODE));
CREATE TABLE TBL_PM_PARTY (ID INTEGER  AUTO_INCREMENT,TYPE VARCHAR(3)  ,STATUS VARCHAR(12) NOT NULL ,REGISTERED_DATE DATETIME  ,CREATED DATETIME  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_PARTY_PK PRIMARY KEY (ID));
CREATE TABLE TBL_PM_IDENTIFICATION (ID INTEGER  AUTO_INCREMENT,TYPE VARCHAR(16) NOT NULL ,VALUE VARCHAR(100) NOT NULL ,PARTY_ID INTEGER NOT NULL ,SOURCE VARCHAR(30)  ,CREATED DATETIME  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_IDENTIFICATION_PK PRIMARY KEY (ID));
CREATE TABLE TBL_PM_CONTACT_MEDIUM (ID INTEGER  AUTO_INCREMENT,PARTY_ID INTEGER NOT NULL ,TYPE VARCHAR(16) NOT NULL ,VALUE VARCHAR(100)  ,IS_MAIN BOOLEAN  ,IS_FAVORITE BOOLEAN  ,SOURCE VARCHAR(30)  ,CREATED_BY VARCHAR(50)  ,CREATED DATETIME  ,CONSTRAINT TBL_PM_CONTACT_MEDIUM_PK PRIMARY KEY (ID));
CREATE TABLE TBL_PM_INDIVIDUAL (PARTY_ID INTEGER NOT NULL ,GENDER VARCHAR(16)  ,BIRTH_DATE DATETIME  ,DEATH_DATE DATETIME  ,NATIONALITY VARCHAR(3)  ,GIVEN_NAMES VARCHAR(200)  ,MIDDLE_NAMES VARCHAR(100)  ,FAMILY_NAMES VARCHAR(100)  ,LEGAL_NAME VARCHAR(500)  ,CREATED DATETIME  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_INDIVIDUAL_PK PRIMARY KEY (PARTY_ID));
CREATE TABLE TBL_PM_ORGANIZATION (PARTY_ID INTEGER NOT NULL ,LEGAL_PERIOD_FROM DATETIME  ,TRADING_NAME VARCHAR(100)  ,INDUSTRY_TYPE VARCHAR(30)  ,COMMERCIAL_DESCRIPTION VARCHAR(400)  ,CREATED DATETIME  ,CREATED_BY VARCHAR(50)  ,rootId INTEGER  ,parentId INTEGER  ,CONSTRAINT TBL_PM_ORGANIZATION_PK PRIMARY KEY (PARTY_ID));
ALTER TABLE TBL_PM_IDENTIFICATION ADD CONSTRAINT TBL_PM_IDENTIFICATION_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_CONTACT_MEDIUM ADD CONSTRAINT TBL_PM_CONTACT_MEDIUM_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_INDIVIDUAL ADD CONSTRAINT TBL_PM_INDIVIDUAL_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_parent_FK FOREIGN KEY (parentId) REFERENCES TBL_PM_ORGANIZATION (PARTY_ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_root_FK FOREIGN KEY (rootId) REFERENCES TBL_PM_ORGANIZATION (PARTY_ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_industryType_FK FOREIGN KEY (INDUSTRY_TYPE) REFERENCES TBL_PM_INDUSTRY_TYPE (CODE);