CREATE TABLE TBL_LOC_COUNTRIES (ISO3 VARCHAR(3) NOT NULL ,NAME VARCHAR(60) NOT NULL ,ISO VARCHAR(2) NOT NULL ,NUMERIC_CODE VARCHAR(5) NOT NULL ,CONSTRAINT TBL_LOC_COUNTRIES_PK PRIMARY KEY (ISO3));
CREATE TABLE TBL_LOC_AREAS (CODE VARCHAR(16) NOT NULL ,PARENT VARCHAR(16)  ,COUNTRY VARCHAR(3) NOT NULL ,TYPE VARCHAR(16) NOT NULL ,NAME VARCHAR(200) NOT NULL ,CONSTRAINT TBL_LOC_AREAS_PK PRIMARY KEY (CODE));
CREATE TABLE TBL_LOC_ADDRESSES (ID serial,COUNTRY VARCHAR(3) NOT NULL ,AREA VARCHAR(16) NOT NULL ,STREET VARCHAR(250)  ,STREET_TYPE VARCHAR(3)  ,NRO VARCHAR(20)  ,ZIP_CODE VARCHAR(30)  ,CITY VARCHAR(100)  ,ADDITIONAL_DATA VARCHAR(100)  ,CONSTRAINT TBL_LOC_ADDRESSES_PK PRIMARY KEY (ID));
CREATE TABLE TBL_PM_INDUSTRY_TYPE (CODE VARCHAR(30) NOT NULL ,NAME VARCHAR(255) NOT NULL ,CONSTRAINT TBL_PM_INDUSTRY_TYPE_PK PRIMARY KEY (CODE));
CREATE TABLE TBL_PM_PARTY (ID serial,TYPE VARCHAR(3)  ,STATUS VARCHAR(12) NOT NULL ,REGISTERED_DATE TIMESTAMP  ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_PARTY_PK PRIMARY KEY (ID));
CREATE TABLE TBL_PM_IDENTIFICATION (ID serial,TYPE VARCHAR(16) NOT NULL ,VALUE VARCHAR(100) NOT NULL ,PARTY_ID INTEGER NOT NULL ,SOURCE VARCHAR(30)  ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_IDENTIFICATION_PK PRIMARY KEY (ID));
CREATE TABLE TBL_PM_CONTACT_MEDIUM (ID serial,PARTY_ID INTEGER NOT NULL ,TYPE VARCHAR(16) NOT NULL ,VALUE VARCHAR(100)  ,IS_MAIN BOOLEAN  ,IS_FAVORITE BOOLEAN  ,SOURCE VARCHAR(30)  ,CREATED_BY VARCHAR(50)  ,CREATED TIMESTAMP  ,CONSTRAINT TBL_PM_CONTACT_MEDIUM_PK PRIMARY KEY (ID));
CREATE TABLE TBL_PM_INDIVIDUAL (PARTY_ID INTEGER NOT NULL ,GENDER VARCHAR(16)  ,BIRTH_DATE TIMESTAMP  ,DEATH_DATE TIMESTAMP  ,NATIONALITY VARCHAR(3)  ,GIVEN_NAMES VARCHAR(200)  ,MIDDLE_NAMES VARCHAR(100)  ,FAMILY_NAMES VARCHAR(100)  ,LEGAL_NAME VARCHAR(500)  ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_INDIVIDUAL_PK PRIMARY KEY (PARTY_ID));
CREATE TABLE TBL_PM_ORGANIZATION (PARTY_ID INTEGER NOT NULL ,LEGAL_PERIOD_FROM TIMESTAMP  ,TRADING_NAME VARCHAR(100)  ,INDUSTRY_TYPE VARCHAR(30)  ,COMMERCIAL_DESCRIPTION VARCHAR(400)  ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,rootId INTEGER  ,parentId INTEGER  ,CONSTRAINT TBL_PM_ORGANIZATION_PK PRIMARY KEY (PARTY_ID));
ALTER TABLE TBL_LOC_AREAS ADD CONSTRAINT TBL_LOC_AREAS_country_FK FOREIGN KEY (COUNTRY) REFERENCES TBL_LOC_COUNTRIES (ISO3);
ALTER TABLE TBL_LOC_AREAS ADD CONSTRAINT TBL_LOC_AREAS_parent_FK FOREIGN KEY (PARENT) REFERENCES TBL_LOC_AREAS (CODE);
ALTER TABLE TBL_LOC_ADDRESSES ADD CONSTRAINT TBL_LOC_ADDRESSES_country_FK FOREIGN KEY (COUNTRY) REFERENCES TBL_LOC_COUNTRIES (ISO3);
ALTER TABLE TBL_LOC_ADDRESSES ADD CONSTRAINT TBL_LOC_ADDRESSES_area_FK FOREIGN KEY (AREA) REFERENCES TBL_LOC_AREAS (CODE);
ALTER TABLE TBL_PM_IDENTIFICATION ADD CONSTRAINT TBL_PM_IDENTIFICATION_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_CONTACT_MEDIUM ADD CONSTRAINT TBL_PM_CONTACT_MEDIUM_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_INDIVIDUAL ADD CONSTRAINT TBL_PM_INDIVIDUAL_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_parent_FK FOREIGN KEY (parentId) REFERENCES TBL_PM_ORGANIZATION (PARTY_ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_root_FK FOREIGN KEY (rootId) REFERENCES TBL_PM_ORGANIZATION (PARTY_ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_industryType_FK FOREIGN KEY (INDUSTRY_TYPE) REFERENCES TBL_PM_INDUSTRY_TYPE (CODE);