CREATE TABLE EMPLOYEES (EMPLOYEEID NUMBER(19,4) NOT NULL ,LASTNAME VARCHAR2(80) NOT NULL ,FIRSTNAME VARCHAR2(80) NOT NULL ,TITLE VARCHAR2(80) NOT NULL ,TITLEOFCOURTESY VARCHAR2(80) NOT NULL ,BIRTHDATE DATE NOT NULL ,HIREDATE DATE NOT NULL ,HOMEPHONE VARCHAR2(80) NOT NULL ,REPORTSTO NUMBER(19,4) NOT NULL ,ADDRESS VARCHAR2(80) NOT NULL ,CITY VARCHAR2(80) NOT NULL ,REGION VARCHAR2(80) NOT NULL ,POSTALCODE VARCHAR2(20) NOT NULL ,COUNTRY VARCHAR2(80) NOT NULL ,CONSTRAINT EMPLOYEES_PK PRIMARY KEY (EMPLOYEEID));
ALTER TABLE EMPLOYEES ADD CONSTRAINT EMPLOYEES_UK UNIQUE (LASTNAME,FIRSTNAME);
CREATE TABLE SHIPPERS (SHIPPERID NUMBER(19,4) NOT NULL ,COMPANYNAME VARCHAR2(80) NOT NULL ,PHONE VARCHAR2(20) NOT NULL ,CONSTRAINT SHIPPERS_PK PRIMARY KEY (SHIPPERID));
ALTER TABLE SHIPPERS ADD CONSTRAINT SHIPPERS_UK UNIQUE (COMPANYNAME);
CREATE TABLE SUPPLIERS (SUPPLIERID NUMBER(19,4) NOT NULL ,COMPANYNAME VARCHAR2(80) NOT NULL ,CONTACTNAME VARCHAR2(80) NOT NULL ,PHONE VARCHAR2(20) NOT NULL ,HOMEPAGE VARCHAR2(200) NOT NULL ,ADDRESS VARCHAR2(80) NOT NULL ,CITY VARCHAR2(80) NOT NULL ,REGION VARCHAR2(80) NOT NULL ,POSTALCODE VARCHAR2(20) NOT NULL ,COUNTRY VARCHAR2(80) NOT NULL ,CONSTRAINT SUPPLIERS_PK PRIMARY KEY (SUPPLIERID));
ALTER TABLE SUPPLIERS ADD CONSTRAINT SUPPLIERS_UK UNIQUE (COMPANYNAME);
CREATE TABLE CATEGORIES (CATEGORYID NUMBER(19,4) NOT NULL ,CATEGORYNAME VARCHAR2(80) NOT NULL ,DESCRIPTION VARCHAR2(1000) NOT NULL ,CONSTRAINT CATEGORIES_PK PRIMARY KEY (CATEGORYID));
ALTER TABLE CATEGORIES ADD CONSTRAINT CATEGORIES_UK UNIQUE (CATEGORYNAME);
CREATE TABLE CUSTOMERS (COUNTRY VARCHAR2(80) NOT NULL ,CUSTOMERID VARCHAR2(5) NOT NULL ,COMPANYNAME VARCHAR2(80) NOT NULL ,CONTACTNAME VARCHAR2(80) NOT NULL ,CONTACTTITLE VARCHAR2(80) NOT NULL ,ADDRESS VARCHAR2(80) NOT NULL ,CITY VARCHAR2(80) NOT NULL ,REGION VARCHAR2(80) NOT NULL ,POSTALCODE VARCHAR2(20) NOT NULL ,CONSTRAINT CUSTOMERS_PK PRIMARY KEY (CUSTOMERID));
CREATE TABLE PRODUCTS (PRODUCTID NUMBER(19,4) NOT NULL ,PRODUCTNAME VARCHAR2(80) NOT NULL ,SUPPLIERID NUMBER(19,4) NOT NULL ,CATEGORYID NUMBER(19,4) NOT NULL ,QUANTITYPERUNIT VARCHAR2(80) NOT NULL ,UNITPRICE NUMBER(19,4) NOT NULL ,UNITSINSTOCK NUMBER(19,4) NOT NULL ,UNITSONORDER NUMBER(19,4) NOT NULL ,REORDERLEVEL NUMBER(19,4) NOT NULL ,DISCONTINUED VARCHAR2(1) NOT NULL ,CONSTRAINT PRODUCTS_PK PRIMARY KEY (CATEGORYID,PRODUCTID,PRODUCTID,SUPPLIERID));
ALTER TABLE PRODUCTS ADD CONSTRAINT PRODUCTS_UK UNIQUE (PRODUCTNAME,SUPPLIERID);
CREATE TABLE ORDERS (ORDERID NUMBER(19,4) NOT NULL ,CUSTOMERID VARCHAR2(5) NOT NULL ,EMPLOYEEID NUMBER(19,4) NOT NULL ,ORDERDATE DATE NOT NULL ,REQUIREDDATE DATE NOT NULL ,SHIPPEDDATE DATE NOT NULL ,SHIPVIA NUMBER(19,4) NOT NULL ,FREIGHT NUMBER(19,4) NOT NULL ,SHIPNAME VARCHAR2(80) NOT NULL ,SHIPADDRESS VARCHAR2(80) NOT NULL ,SHIPCITY VARCHAR2(80) NOT NULL ,SHIPREGION VARCHAR2(80) NOT NULL ,SHIPPOSTALCODE VARCHAR2(20) NOT NULL ,SHIPCOUNTRY VARCHAR2(80) NOT NULL ,CONSTRAINT ORDERS_PK PRIMARY KEY (CUSTOMERID,EMPLOYEEID,ORDERID,ORDERID));
CREATE TABLE "Order Details" (ORDERID NUMBER(19,4) NOT NULL ,PRODUCTID NUMBER(19,4) NOT NULL ,UNITPRICE NUMBER(19,4) NOT NULL ,QUANTITY NUMBER(19,4) NOT NULL ,DISCOUNT NUMBER(19,4) NOT NULL ,CONSTRAINT "Order Details_PK" PRIMARY KEY (ORDERID,PRODUCTID,ORDERID,PRODUCTID));
ALTER TABLE EMPLOYEES ADD CONSTRAINT EMPLOYEES_reportsTo_FK FOREIGN KEY (REPORTSTO) REFERENCES EMPLOYEES (EMPLOYEEID);
ALTER TABLE PRODUCTS ADD CONSTRAINT PRODUCTS_category_FK FOREIGN KEY (CATEGORYID) REFERENCES CATEGORIES (CATEGORYID);
ALTER TABLE PRODUCTS ADD CONSTRAINT PRODUCTS_supplier_FK FOREIGN KEY (SUPPLIERID) REFERENCES SUPPLIERS (SUPPLIERID);
ALTER TABLE ORDERS ADD CONSTRAINT ORDERS_customer_FK FOREIGN KEY (CUSTOMERID) REFERENCES CUSTOMERS (CUSTOMERID);
ALTER TABLE ORDERS ADD CONSTRAINT ORDERS_employee_FK FOREIGN KEY (EMPLOYEEID) REFERENCES EMPLOYEES (EMPLOYEEID);
ALTER TABLE "Order Details" ADD CONSTRAINT "Order Details_order_FK" FOREIGN KEY (ORDERID) REFERENCES ORDERS (ORDERID);
ALTER TABLE "Order Details" ADD CONSTRAINT "Order Details_product_FK" FOREIGN KEY (PRODUCTID) REFERENCES PRODUCTS (PRODUCTID);