CREATE TABLE Categories (CategoryID INTEGER NOT NULL ,CategoryName VARCHAR(80) NOT NULL ,Description VARCHAR(1000)  ,PRIMARY KEY (CategoryID));
ALTER TABLE Categories ADD CONSTRAINT Categories_UK UNIQUE (CategoryName);
CREATE TABLE Customers (CustomerID VARCHAR(5) NOT NULL ,CompanyName VARCHAR(80) NOT NULL ,ContactName VARCHAR(80)  ,ContactTitle VARCHAR(80)  ,Address VARCHAR(80)  ,City VARCHAR(80)  ,Region VARCHAR(80)  ,PostalCode VARCHAR(20)  ,Country VARCHAR(80)  ,PRIMARY KEY (CustomerID));
CREATE TABLE Employees (EmployeeID INTEGER NOT NULL ,LastName VARCHAR(80) NOT NULL ,FirstName VARCHAR(80) NOT NULL ,Title VARCHAR(80)  ,TitleOfCourtesy VARCHAR(80)  ,BirthDate DATETIME  ,HireDate DATETIME  ,HomePhone VARCHAR(80)  ,ReportsTo INTEGER  ,Address VARCHAR(80)  ,City VARCHAR(80)  ,Region VARCHAR(80)  ,PostalCode VARCHAR(20)  ,Country VARCHAR(80)  ,PRIMARY KEY (EmployeeID));
ALTER TABLE Employees ADD CONSTRAINT Employees_UK UNIQUE (LastName,FirstName);
CREATE TABLE Shippers (ShipperID INTEGER NOT NULL ,CompanyName VARCHAR(80) NOT NULL ,Phone VARCHAR(20)  ,PRIMARY KEY (ShipperID));
ALTER TABLE Shippers ADD CONSTRAINT Shippers_UK UNIQUE (CompanyName);
CREATE TABLE Suppliers (SupplierID INTEGER NOT NULL ,CompanyName VARCHAR(80) NOT NULL ,ContactName VARCHAR(80)  ,Phone VARCHAR(20)  ,HomePage VARCHAR(200)  ,Address VARCHAR(80)  ,City VARCHAR(80)  ,Region VARCHAR(80)  ,PostalCode VARCHAR(20)  ,Country VARCHAR(80)  ,PRIMARY KEY (SupplierID));
ALTER TABLE Suppliers ADD CONSTRAINT Suppliers_UK UNIQUE (CompanyName);
CREATE TABLE Products (ProductID INTEGER NOT NULL ,ProductName VARCHAR(80) NOT NULL ,SupplierID INTEGER NOT NULL ,CategoryID INTEGER NOT NULL ,QuantityPerUnit VARCHAR(80)  ,UnitPrice DECIMAL(10,4)  ,UnitsInStock DECIMAL(10,4)  ,UnitsOnOrder DECIMAL(10,4)  ,ReorderLevel DECIMAL(10,4)  ,Discontinued BIT  ,PRIMARY KEY (CategoryID,ProductID,ProductID,SupplierID));
ALTER TABLE Products ADD CONSTRAINT Products_UK UNIQUE (ProductName,SupplierID);
CREATE TABLE Orders (OrderID INTEGER NOT NULL ,CustomerID VARCHAR(5) NOT NULL ,EmployeeID INTEGER NOT NULL ,OrderDate DATETIME  ,RequiredDate DATE  ,ShippedDate DATE  ,ShipVia INTEGER  ,Freight DECIMAL(10,4)  ,ShipName VARCHAR(80)  ,ShipAddress VARCHAR(80)  ,ShipCity VARCHAR(80)  ,ShipRegion VARCHAR(80)  ,ShipPostalCode VARCHAR(20)  ,ShipCountry VARCHAR(80)  ,PRIMARY KEY (CustomerID,EmployeeID,OrderID,OrderID));
CREATE TABLE [Order Details] (OrderID INTEGER NOT NULL ,ProductID INTEGER NOT NULL ,UnitPrice DECIMAL(10,4)  ,Quantity DECIMAL(10,4)  ,Discount DECIMAL(10,4)  ,PRIMARY KEY (OrderID,ProductID,OrderID,ProductID));
CREATE INDEX Customers_customersName ON Customers (CompanyName);
ALTER TABLE Employees ADD CONSTRAINT Employees_reportsTo_FK FOREIGN KEY (ReportsTo) REFERENCES Employees (ReportsTo);
ALTER TABLE Products ADD CONSTRAINT Products_category_FK FOREIGN KEY (CategoryID) REFERENCES Products (CategoryID);
ALTER TABLE Products ADD CONSTRAINT Products_supplier_FK FOREIGN KEY (SupplierID) REFERENCES Products (SupplierID);
CREATE INDEX Orders_ordersOrderDate ON Orders (OrderDate);
CREATE INDEX Orders_ordersShippedDate ON Orders (ShippedDate);
ALTER TABLE Orders ADD CONSTRAINT Orders_customer_FK FOREIGN KEY (CustomerID) REFERENCES Orders (CustomerID);
ALTER TABLE Orders ADD CONSTRAINT Orders_employee_FK FOREIGN KEY (EmployeeID) REFERENCES Orders (EmployeeID);
ALTER TABLE [Order Details] ADD CONSTRAINT [Order Details_order_FK] FOREIGN KEY (OrderID) REFERENCES [Order Details] (OrderID);
ALTER TABLE [Order Details] ADD CONSTRAINT [Order Details_product_FK] FOREIGN KEY (ProductID) REFERENCES [Order Details] (ProductID);