CREATE TABLE TBL_PM_INDUSTRY_TYPE (CODE VARCHAR(30) NOT NULL ,NAME VARCHAR(255) NOT NULL ,CONSTRAINT TBL_PM_INDUSTRY_TYPE_PK PRIMARY KEY (CODE));
CREATE TABLE TBL_PM_PARTY (PARTY_ID serial,INDIVIDUAL_ID INTEGER  ,ORGANIZATION_ID INTEGER  ,STATUS VARCHAR(16) NOT NULL ,REGISTER_DATE TIMESTAMP NOT NULL ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_PARTY_PK PRIMARY KEY (PARTY_ID));
CREATE TABLE TBL_PM_IDENTIFICATION (IDENTIFICATION_ID serial,TYPE VARCHAR(16) NOT NULL ,PARTY_ID INTEGER NOT NULL ,VALUE VARCHAR(100) NOT NULL ,SOURCE VARCHAR(30)  ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_IDENTIFICATION_PK PRIMARY KEY (IDENTIFICATION_ID));
CREATE TABLE TBL_PM_PARTY_CONTACT_MEDIUM (PARTY_CONTACT_MEDIUM_ID serial,TYPE VARCHAR(16) NOT NULL ,PARTY_ID INTEGER NOT NULL ,VALUE VARCHAR(100) NOT NULL ,IS_MAIN BOOLEAN  ,IS_FAVORITE BOOLEAN  ,SOURCE VARCHAR(30)  ,CREATED_BY VARCHAR(50)  ,CREATED TIMESTAMP  ,CONSTRAINT TBL_PM_PARTY_CONTACT_MEDIUM_PK PRIMARY KEY (PARTY_CONTACT_MEDIUM_ID));
CREATE TABLE TBL_PM_INDIVIDUAL (INDIVIDUAL_ID serial,PARTY_ID INTEGER NOT NULL ,GENDER VARCHAR(16)  ,MARITAL_STATUS VARCHAR(16)  ,BIRTH_DATE TIMESTAMP  ,DEATH_DATE TIMESTAMP  ,NATIONALITY_CODE VARCHAR(3)  ,GIVEN_NAMES VARCHAR(200)  ,MIDDLE_NAMES VARCHAR(100)  ,FAMILY_NAMES VARCHAR(100)  ,LEGAL_NAME VARCHAR(500)  ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_INDIVIDUAL_PK PRIMARY KEY (INDIVIDUAL_ID));
CREATE TABLE TBL_PM_ORGANIZATION (ORGANIZATION_ID serial,PARTY_ID INTEGER NOT NULL ,LEGAL_PERIOD_FROM TIMESTAMP  ,TRADING_NAME VARCHAR(100)  ,INDUSTRY_TYPE_CODE VARCHAR(30)  ,COMMERCIAL_DESCRIPTION VARCHAR(400)  ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_ORGANIZATION_PK PRIMARY KEY (ORGANIZATION_ID));
ALTER TABLE TBL_PM_IDENTIFICATION ADD CONSTRAINT TBL_PM_IDENTIFICATION_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (PARTY_ID);
ALTER TABLE TBL_PM_PARTY_CONTACT_MEDIUM ADD CONSTRAINT TBL_PM_PARTY_CONTACT_MEDIUM_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (PARTY_ID);
ALTER TABLE TBL_PM_INDIVIDUAL ADD CONSTRAINT TBL_PM_INDIVIDUAL_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (PARTY_ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (PARTY_ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_industryType_FK FOREIGN KEY (INDUSTRY_TYPE_CODE) REFERENCES TBL_PM_INDUSTRY_TYPE (CODE);