CREATE TABLE TBL_PM_INDUSTRY_TYPE (CODE VARCHAR(30) NOT NULL ,NAME VARCHAR(255) NOT NULL ,CONSTRAINT TBL_PM_INDUSTRY_TYPE_PK PRIMARY KEY (CODE));
CREATE TABLE TBL_PM_PARTY (ID serial,TYPE VARCHAR(3)  ,STATUS VARCHAR(12) NOT NULL ,REGISTER_DATE TIMESTAMP NOT NULL ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_PARTY_PK PRIMARY KEY (ID));
CREATE TABLE TBL_PM_IDENTIFICATION (ID serial,TYPE VARCHAR(16) NOT NULL ,VALUE VARCHAR(100) NOT NULL ,PARTY_ID INTEGER NOT NULL ,SOURCE VARCHAR(30)  ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,CONSTRAINT TBL_PM_IDENTIFICATION_PK PRIMARY KEY (ID));
CREATE TABLE TBL_PM_CONTACT_MEDIUM (ID serial,PARTY_ID INTEGER NOT NULL ,TYPE VARCHAR(16) NOT NULL ,VALUE VARCHAR(100)  ,IS_MAIN BOOLEAN  ,IS_FAVORITE BOOLEAN  ,SOURCE VARCHAR(30)  ,CREATED_BY VARCHAR(50)  ,CREATED TIMESTAMP  ,CONSTRAINT TBL_PM_CONTACT_MEDIUM_PK PRIMARY KEY (ID));
CREATE TABLE TBL_PM_INDIVIDUAL (PARTY_ID INTEGER NOT NULL ,GENDER VARCHAR(16)  ,BIRTH_DATE TIMESTAMP  ,DEATH_DATE TIMESTAMP  ,NATIONALITY_CODE VARCHAR(3)  ,GIVEN_NAMES VARCHAR(200)  ,MIDDLE_NAMES VARCHAR(100)  ,FAMILY_NAMES VARCHAR(100)  ,LEGAL_NAME VARCHAR(500)  ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,code VARCHAR(32) NOT NULL ,CONSTRAINT TBL_PM_INDIVIDUAL_PK PRIMARY KEY (PARTY_ID));
ALTER TABLE TBL_PM_INDIVIDUAL ADD CONSTRAINT TBL_PM_INDIVIDUAL_UK UNIQUE (code);
CREATE TABLE TBL_PM_ORGANIZATION (PARTY_ID INTEGER NOT NULL ,LEGAL_PERIOD_FROM TIMESTAMP  ,TRADING_NAME VARCHAR(100)  ,INDUSTRY_TYPE_CODE VARCHAR(30)  ,COMMERCIAL_DESCRIPTION VARCHAR(400)  ,CREATED TIMESTAMP  ,CREATED_BY VARCHAR(50)  ,code VARCHAR(32) NOT NULL ,rootId INTEGER  ,parentId INTEGER  ,CONSTRAINT TBL_PM_ORGANIZATION_PK PRIMARY KEY (PARTY_ID));
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_UK UNIQUE (code);
ALTER TABLE TBL_PM_IDENTIFICATION ADD CONSTRAINT TBL_PM_IDENTIFICATION_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_CONTACT_MEDIUM ADD CONSTRAINT TBL_PM_CONTACT_MEDIUM_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_INDIVIDUAL ADD CONSTRAINT TBL_PM_INDIVIDUAL_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_party_FK FOREIGN KEY (PARTY_ID) REFERENCES TBL_PM_PARTY (ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_parent_FK FOREIGN KEY (parentId) REFERENCES TBL_PM_ORGANIZATION (PARTY_ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_root_FK FOREIGN KEY (rootId) REFERENCES TBL_PM_ORGANIZATION (PARTY_ID);
ALTER TABLE TBL_PM_ORGANIZATION ADD CONSTRAINT TBL_PM_ORGANIZATION_industryType_FK FOREIGN KEY (INDUSTRY_TYPE_CODE) REFERENCES TBL_PM_INDUSTRY_TYPE (CODE);