CREATE TABLE Suppliers (SupplierID INTEGER  AUTO_INCREMENT,CompanyName VARCHAR(80) NOT NULL ,ContactName VARCHAR(80)  ,Phone VARCHAR(20)  ,HomePage VARCHAR(200)  ,Address VARCHAR(80)  ,City VARCHAR(80)  ,Region VARCHAR(80)  ,PostalCode VARCHAR(20)  ,Country VARCHAR(80)  ,CONSTRAINT Suppliers_PK PRIMARY KEY (SupplierID));
